html.script:
var is_not_done = $('#progress_memory_not_done').val();
var is_error = $('#progress_memory_error').val();
if (is_not_done == "1") {
    msg_dom = $('#view_incoming_in_progress')
    msg_dom.show();
}
if (is_error == "1") {
    msg_dom = $('#view_incoming_in_progress_error')
    msg_dom.show()
    msg_dom_done = $('#view_incoming_in_progress')
    msg_dom_done.hide()
}
